const SHA256 = require('crypto-js/sha256');
const EC = require('elliptic').ec;
const ec = new EC('secp256k1');

export class Transaction {
    constructor(fromAddress, toAddress, amount, payload) {
        this.fromAddress = fromAddress;
        this.toAddress = toAddress;
        this.amount = amount;
        this.payload = payload;
        this.timestamp = Date.now();
    }

    calculateHash(){
        return SHA256(this.fromAddress + this.toAddress, + this.amount + this.payload + this.timestamp).toString();
    }

    signTransaction(signingKey){
        if(signingKey.getPublic('hex') !== this.fromAddress){
            throw new Error('You cannot sign transactions for other wallets!');
        }

        const hashTransaction = this.calculateHash();
        const sig = signingKey.sign(hashTransaction, 'base64');
        this.signature = sig.toDER('hex');
    }

    isValid(){
       //TODO
        if(this.fromAddress === null) return false; 

       if(!this.signature || this.signature.length === 0){
           throw new Error('No signature in this transaction');
       }

       const publicKey = ec.keyFromPublic(this.fromAddress, 'hex');
       return publicKey.verify(this.calculateHash(), this.signature);
    }
}

export class Block {
    constructor(timestamp, transactions, previousHash = '') {
        this.timestamp = timestamp;
        //TODO: change data to payload and add amount
        this.transactions = transactions;
        this.previousHash = previousHash;
        this.hash = this.calculateHash();
        this.nonce = 0;
    }

    calculateHash() {
        return SHA256(this.index + this.timestamp + JSON.stringify(this.transactions)
         + this.previousHash + this.nonce).toString();
    }

    mineBlock(difficulty) {
        while(this.hash.substring(0, difficulty) !== 
        Array(difficulty + 1).join('0')) {
            this.nonce++;
            this.hash = this.calculateHash();
        }

        console.log('Block mined: ' + this.hash);
    }

    hasValidTransactions(){
        for (const tx of this.transactions){
            if(!tx.isValid()){
                return false;
            }
        }

        return true;
    }
}


export class Blockchain{
    constructor(){
        this.chain = [this.createGenesisBlock()];
        this.difficulty = 2;
        this.pendingTransactions = [];
        this.miningReward = 100;
    }

    createGenesisBlock() {
        return new Block('01.01.2019', 'Genesis block', '0');
    }

    getLatestBlock() {
        return this.chain[this.chain.length - 1];
    }

    minePendingTransactions(miningRewardAddress) {
        const rewardTx = new Transaction(null, miningRewardAddress, this.miningReward);
        this.pendingTransactions.push(rewardTx);

        //TODO: Miners should choose which tx they want to mine
        let block = new Block(Date.now(), this.pendingTransactions, this.getLatestBlock().hash);
        block.mineBlock(this.difficulty);

        console.log('Block successfully mined!');
        this.chain.push(block);

        this.pendingTransactions = [];
    }

    addTransaction(transaction){

        if(!transaction.fromAddress || !transaction.toAddress){
            throw new Error('Transaction must include from and to address');
        }

        if(!transaction.isValid()){
            throw new Error('Cannot add invalid transaction to the chain!');
        }

        this.pendingTransactions.push(transaction);
    }

    getBalanceOfAddress(address){
        let balance = 0;

        for(const block of this.chain){
            for(const trans of block.transactions){
                if (trans.fromAddress === address){
                    balance -= trans.amount;
                }

                if (trans.toAddress === address){
                    balance += trans.amount;
                }
            }
        }

        return balance;
    }

    isChainValid() {
        for(let i = 1; i < this.chain.length; i++) {
            const currentBlock = this.chain[i];
            const previousBlock = this.chain[i - 1];

            if(!currentBlock.hasValidtransactions){
                return false;
            }

            if(currentBlock.hash !== currentBlock.calculateHash()) {
                return false; 
            }

            if(currentBlock.previousHash !== previousBlock.hash){
                return false;
            }
        }

        return true;
    }
}

const fs = require('fs');
const path = require('path');
const {Blockchain, Transaction} = require('./blockchain');
const EC = require('elliptic').ec;
const ec = new EC('secp256k1');

/**
 * Minacoin Transaction Manager
 * @example 
 * let miner = new Miner();
 * miner.initTransactions();
 */
export class Miner {
    /**
     * Constructor: Setting up Miner
     */
    constructor() {
        const myKey = ec.keyFromPrivate('874721b0cfd73d79c76c50d5b2ee59f7d9fe9c5743bc45c015c713903b46b7c4');
        this.myWalletAddress = myKey.getPublic('hex');
        this.myKey = myKey;
        const key = ec.genKeyPair().getPublic().encode('hex'); // generates a random key:
        const theirKey = ec.keyFromPrivate(key);
        this.theirWalletAddress = theirKey.getPublic('hex');
        this.minaCoin = new Blockchain(); 
    }

    /**
     * Gets cycles from files in JSON format
     * @todo Here could be a logger
     * @return {undefined}
     */
    getCycles() {
        try {
            const folderName = '../data/';

            return fs.readdirSync(folderName).map(file => {
                return require(path.resolve(folderName, file))['cycle'];
            });
        }
        catch (err) {
            // TODO Here could be a logger
            console.log('Getting cycles failed ' + err);

            return null;
        }
    }

    /**
     * Sends a transaction
     * @param {string} payload Contains a string of a cycle
     * @return {undefined}
     */
    sendTransaction (payload) {
        if(payload === undefined || payload === '') {
            throw new Error('Payload is not defined');
        }
        const tx1 = new Transaction(this.myWalletAddress, this.theirWalletAddress, 10, payload);
        tx1.signTransaction(this.myKey);
        this.minaCoin.addTransaction(tx1);
    }

    /**
     * @summary Gets the latest Block
     * @return {Block}
     */
    getLatestBlock () {
        return this.minaCoin.getLatestBlock();
    }

    /**
     * Gets user's balance
     * @return {number}
     */
    getMyBalance () {
        return this.minaCoin.getBalanceOfAddress(this.myWalletAddress);
    }

     /**
     * Gets other user's balance
     * @return {number}
     */
    getTheirBalance () {
        return this.minaCoin.getBalanceOfAddress(this.theirWalletAddress);
    }

    /** 
     * Prepares data for view
     * @return {Object}
     */
    getData() {
        return {
            theirBalance: this.getTheirBalance(),
            myBalance: this.getMyBalance(),
            latestBlock: this.getLatestBlock()
        };
    }

    /**
     * @summary Initiates transaction
     * @emits getCycles
     * @emits sendTransaction
     * @emits minaCoin.minePendingTransactions
     * @return {undefined}
     */
    initTransactions() {
        const cycles = this.getCycles();

        for (let index = 0; index < cycles.length; index++) {
            let cycle = cycles[index];
            this.sendTransaction(cycle);
        }

        console.log('Starting the miner');
        this.minaCoin.minePendingTransactions(this.myWalletAddress);
    }
}

const fs = require('fs');
const PDFDocument = require('pdfmake');

/**
 * Creates PDF with dynamic data
 * @example 
 * let pdfMaker = new PDFMaker(minaCoinData);
 * pdfMaker.createPDF();
 */
export class PDFMaker {
    /**
     * @param {Object} minaCoinData
    */
    constructor(minaCoinData) {
        const fonts = {
            Roboto: {
                normal: __dirname  + '/../fonts/Roboto-Regular.ttf',
                bold: __dirname  + '/../fonts/Roboto-Medium.ttf',
                italics: __dirname  + '/../fonts/Roboto-RegularItalic.ttf',
                bolditalics: __dirname  + '/../fonts/Roboto-MediumItalic.ttf',
            }
        };
        this.minaCoinData = minaCoinData;
        this.document = new PDFDocument(fonts);
        this.definitions = {};
        this.options = {};
    }

    /**
     * Creates the table and styling for the PDF
     * @todo Document Builder Pattern fits here
     * @return {undefined}
     */
    defineDocument () {
        // TODO Document Builder Pattern fits here
        const content = {};
        content.layout = 'lightHorizontalLines';
        content.table = {};
        content.table.headerRows = 1;
        content.table.width = [ '*', '*', 100, '*' ];
        content.table.body = [];
        content.table.body.push([
            { text: 'Quantity', bold: true },
            { text: 'Description', bold: true },
            { text: 'Unit Price', bold: true },
            { text: 'Total', bold: true }
        ]);

        this.minaCoinData.latestBlock.transactions.forEach((transaction) => {
            content.table.body.push([
                transaction.amount,
                'Transaction',
                transaction.amount,
                ''
            ]);
        })
        this.definitions.content = [];
        this.definitions.content.push(content);
    }

    /**
     * Creates the document
     * @return {any}
     */
    createPDF() {
        this.defineDocument();
        const output = this.document.createPdfKitDocument(this.definitions, this.options);
        output.pipe(fs.createWriteStream(__dirname  + '/../pdf/billing.pdf'));
        return output.end(); // finalizes document
    }
}

const {PDFMaker} = require('../components/pdfMaker');

/**
 * Defines some routes
 */
module.exports = function(app, miner) {
    const minaCoinData = miner.getData();

    /**
     * Request for index
     * @param {any} res Response
     * @param {any} req Request
     * @return {any} Renders Dashboard view
     */
    app.get('/', (req, res) => {
      console.log('Visited page: Dashboard.');
      let dashboardData = {
        title: 'Wallet',
        minaCoinData: minaCoinData
      };

      return res.render('dashboard', dashboardData);
    });

    /**
     * Request for Billing PDF
     * @param {any} res Response
     * @param {any} req Request
     * @return {any} Downloads billing.pdf
     */
    app.get('/print', (req, res) => {
      const pdf = new PDFMaker(minaCoinData)
      pdf.createPDF();

      return res.download('lib/pdf/billing.pdf');
    });

    /**
     * Request for /data, A more detailed view
     * @param {any} res Response
     * @param {any} req Request
     * @return {any} Renders Dashboard view
     */
    app.get('/data', (req, res) => {
        return res.render('data', {
            title: 'Data',
            minaCoinData: miner.getData()
          }, console.log('Visited page: Data.'));
    });

    /**
     * Request for /mine. How much can you mine?
     * @param {any} res Response
     * @param {any} req Request
     * @return {any} Renders Mining view
     */
    app.get('/mine', (req, res) => {
      miner.initTransactions();

      let dashboardData = {
        title: 'Dashboard',
        minaCoinData: miner.getData()
      };

      return res.render('dashboard', dashboardData);
    });
};

